package com.jisu.springmongo.exception;import com.mongodb.MongoWriteException;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.RestControllerAdvice;import java.util.HashMap;import java.util.Map;@RestControllerAdvicepublic class GlobalExceptionHandler {    @ExceptionHandler(MongoWriteException.class)    public ResponseEntity<?> handleMongoWriteException(){        Map errorMap = new HashMap();        errorMap.put("MSG","Duplicate Email or Password");        return new ResponseEntity<>(errorMap, HttpStatus.BAD_REQUEST);    }}