package com.jisu.springmongo.model;import com.jisu.springmongo.enums.Gender;import com.jisu.springmongo.enums.Role;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.index.Indexed;import org.springframework.data.mongodb.core.mapping.Document;import org.springframework.data.mongodb.core.mapping.Field;@NoArgsConstructor@AllArgsConstructor@Data@Documentpublic class UserEO {    @Id    private String id;    @Indexed(unique = true)    private String email;    private String fullName;    private String password;    @Indexed(unique = true)    private String mobileNumber;    @Field    private Gender gender;    @Field    private Role role;    private String photo;}